/*
POST /api/auth/register
{
    username: 'velopert',
    password: 'mypass123'
}
*/

       const register = async (ctx) => {
  //Request Body 검증하기
  const schema = Joi.object().keys({
    usernmae: Joi.string().alphanum().min(3).max(20).required(),
    password: Joi.string().required(),
  });
  const result = schema.validate(ctx.request.body);
  if (result.error) {
    ctx.status = 400;
    ctx.body = result.error;
    return;
  }

  const { username, password } = ctx.request.body;
  try {
    //username이 이미 존재하는지 확인
    const exists = await User.findByUsername(username);
    if (exists) {
      ctx.status = 409; // Conflict
      return;
    }

    const user = new User({
      username,
    });
    await user.setPassword(password); // 비밀번호 설정 setPasswrod 인스턴스 함수 사용
    await user.save(); //데이터베이스에 저장

    //응답할 데이터에서 hashedPassword 필드제거
    const data = user.toJSON(); //hashedPassword 필드가 응답되지 않도록 데이터를 JSON으로 변환한 후 delete를 통해 필드에서 삭제
    delete data.hashedPassword;
    ctx.body = user.serialize();
  } catch (e) {
    ctx.throw(500, e);
  }
};
